import { Button , Row, Col, InputGroup, Form} from "react-bootstrap";
import { useEffect, useState } from "react";
import Lista from './Lista';
import CONFIG from './config/config';
import Filtrar from './Filtrar.js';

export default function SearchPage(props) {
    const [query, setQuery] = useState("");
    const [printers, setprinters] = useState(props.theprinters);
    
    const filter=()=>{
        let input = query;
        let printersFiltrado;
        printersFiltrado = printers.filter((items) => items.title.toLowerCase().includes(input.toLowerCase()));
        setprinters(printersFiltrado);
    }

    const categ = props.theprinters.reduce((accumulator, currentValue) => {
          if (!accumulator.includes(currentValue.category)) {
            accumulator.push(currentValue.category);
          }
          return accumulator;
    }, []);

    return (
        <div>
            <h2 id="catálogo">catálogo</h2> 
            <Row>
                <Col> 
                    <h5>Buscar</h5>
                    <Row>
                        <InputGroup style={{width: "80%",  margin: '0 auto'}} >
                            <Form.Control id='filtro' type="text" value={query} onChange={(e) => setQuery(e.target.value)}/>
                        </InputGroup>   
                    </Row>
                    <Row>
                        <Button id="buscador" variant="secondary"  onClick={filter} style={{width: "100px",  margin: '0 auto',marginTop: '5px'}} >Buscador</Button>{' '}
                    </Row>
                    </Col>
                <Col>
                    <Filtrar categorias={categ} setprinters={setprinters} theprinters={props.theprinters} /> 
                </Col>
            </Row>
            <Row>
                <Lista printers={printers} />
            </Row>   
        </div>
    )
}
