import { Form} from "react-bootstrap";

export default function Lista(props) {

    const cambiarCategoria = (category) => {
        const seleccionar = category.target.value;
        filtrarPorCategoria(seleccionar);
    };
    

    const filtrarPorCategoria = (categoria) => {
        if(categoria!="All"){
            let productos = [];
            productos = props.theProducts.filter((items) => items.category.includes(categoria));
            console.log(productos);
            props.setProducts(productos);
        }else{
            props.setProducts(props.theProducts);
        }

    }
   

    return(<div>
        
        <h5>Filtrar</h5>
        <Form.Select id="selector" onChange={cambiarCategoria} style={{width: "300px",  margin: '0 auto'}}>
            <option>All</option>
                        
            {props.categorias.map((categoria, index) => (
                <option key={index} value={categoria}>
                    {categoria}
                </option>
            ))}
        </Form.Select>

    </div>);

}
