    let downloadProducts;


    if(CONFIG.use_server){
      try {
        const res = await fetch(SERVER_URL);
        downloadProducts = await res.json();
      } catch (e) {
      }
    }else{
      downloadProducts=mockdata;
    }
    setTheProducts(downloadProducts);

  }

  useEffect(() => {
    setLoading(true);
      async function fetchData() {
        await download();
       setTimeout(()=>{
          setLoading(false);
        },500);		
    }
    fetchData();
  }, []);


  return (
    <div className="App">
      <Header/>
      {loading ? <img id="loading" src={process.env.PUBLIC_URL + "/spinner.gif"} className="spinner" alt="spinner" />:

        <Routes>
          <Route path="/" element={<SearchPage theproducts={theproducts.products} setTheProducts={setTheProducts} />} />
          <Route path="/products/:productId" element={<Location theproducts={theproducts.products}/>}/>
          <Route path="/*" element={<Error/>}/>

