import { useState } from 'react';
// import { Navbar, Nav, Form, FormControl, Button } from 'react-bootstrap';
// import { Card , Container, Row, Col, InputGroup} from "react-bootstrap";
import { Navbar, Nav, Form, FormControl, Button, Card, Container, Row, Col, InputGroup } from 'react-bootstrap';

function NavigationBar({ roll, query, setQuery, queryUbica, setQueryUbica }) {
  {/* <NavBar roll={roll} query={query} setQuery={setQuery} queryUbica={queryUbica} setQueryUbica={setQueryUbica}/> */}
  
  const [localQuery, setLocalQuery] = useState(query);
  const [localQueryUbica, setLocalQueryUbica] = useState(queryUbica);

  const handleQueryChange = (event) => {
    setLocalQuery(event.target.value);
  };

  const handleQueryUbicaChange = (event) => {
    setLocalQueryUbica(event.target.value);
  };

  const handleSearchClick = () => {
    setQuery(localQuery);
    setQueryUbica(localQueryUbica);
  };

  return (
    <Navbar bg="light" expand="lg">
      <Navbar.Brand href="#home">Logo</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="mr-auto">
          {roll === "diseñador" || roll === "fabricante" ?
            <Nav.Link href="info">Info</Nav.Link>
          : null
          }          

          
          <Nav.Link href="#link">Link</Nav.Link>
        </Nav>
        <Form inline className="mx-auto">
          <Row className="w-100">
            <InputGroup className="mb-3" size="sm">
              <FormControl placeholder="Search" aria-label="Search" aria-describedby="basic-addon2" value={localQuery} onChange={handleQueryChange} onClick={() => setLocalQuery('')}/>
              <Button variant="outline-secondary" id="button-addon2" onClick={handleSearchClick}> Search </Button>
            </InputGroup>
          </Row>
          <Row className="w-100">
            <InputGroup className="mb-3" size="sm">
              <FormControl placeholder="Location" aria-label="Search" aria-describedby="basic-addon2" value={localQueryUbica} onChange={handleQueryUbicaChange} onClick={() => setLocalQueryUbica('')}/>
              <Button variant="outline-secondary" id="button-addon2" onClick={handleSearchClick}>Search</Button>
            </InputGroup>
          </Row>
        </Form>
        {roll === "diseñador"   ?
            <Nav.Link href="carritocompra">Carrito</Nav.Link>
          : null
        }
        {roll === "fabricante"   ?
            <Nav.Link href="controlprinters">Imprsoras</Nav.Link>
          : null
        }

        {/* Creame botones de login diseñador color azul */}
        <Nav.Link href="loginDiseñador">loginDiseñador</Nav.Link>
        <Nav.Link href="loginFabricante">loginDiseñador</Nav.Link>

        <Button variant="outline-success" href="loginDiseñador">loginDiseñador</Button>
        <Button variant="outline-success" href="loginFabricante">loginFabricante</Button>
      </Navbar.Collapse>
    </Navbar>
  );
}

export default NavigationBar;
